insert into sentences (id, bookId, chapterId, pl, en) value(7807, 11, 15, "Zrobiliśmy badanie na temat, jak reklama wpływa na konsumentów.", "We did research on how advertising affects consumers.");
insert into sentences (id, bookId, chapterId, pl, en) value(7808, 11, 15, "Jakich danych liczbowych użyliście jako punktu odniesienia w badaniu?", "What figures did you use as a baseline for the study?");
insert into sentences (id, bookId, chapterId, pl, en) value(7809, 11, 15, "Pierwszym etapem było zbieranie danych, po czym nastąpiła ich analiza.", "The first stage was data collection followed by its analysis.");
insert into sentences (id, bookId, chapterId, pl, en) value(7810, 11, 15, "Ponadto, przeprowadziliśmy badania opinii i poprosiliśmy naszych klientów o wypełnienie kwestionariuszy.", "Besides this, we conducted opinion polls and asked our customers to fill in questionnaires.");
insert into sentences (id, bookId, chapterId, pl, en) value(7811, 11, 15, "Przeprowadziliśmy kilka badań. Odkrycia z całego badania będą wkrótce opublikowane na naszej stronie internetowej.", "We carried out several studies. The findings of all the research will be published on our website soon.");
insert into sentences (id, bookId, chapterId, pl, en) value(7812, 11, 15, "Celem tego ostatniego badania jest to, aby dowiedzieć się, jakie marki konsumenci wybierają i dlaczego.", "The purpose of this recent study is to find out what brands consumers choose and why.");
insert into sentences (id, bookId, chapterId, pl, en) value(7813, 11, 15, "Jakie czynniki wzięliście pod uwagę?", "What factors did you take into consideration/account?");
insert into sentences (id, bookId, chapterId, pl, en) value(7814, 11, 15, "Aby być częścią naszego zespołu ds. badań i rozwoju, musisz być dobry w liczbach i wykazać się inicjatywą.", "In order to be part of our R&D team, you have to be good with figures and use your initiative.");
insert into sentences (id, bookId, chapterId, pl, en) value(7815, 11, 15, "Sondaż, który przeprowadziliśmy pokazał, że ponad 50% ludności jest za reformą.", "The survey that we carried out showed that over 50% of the population is in favour of the reform.");
insert into sentences (id, bookId, chapterId, pl, en) value(7816, 11, 15, "40% ankietowanych było przeciwnych reformie.", "40% of those polled were against the reform.");
insert into sentences (id, bookId, chapterId, pl, en) value(7817, 11, 15, "Czy możesz rozbić/podzielić wyniki na kategorie według wykształcenia, płci i zarobków?", "Can you break down the results into categories according to education, sex and earnings?");
insert into sentences (id, bookId, chapterId, pl, en) value(7818, 11, 15, "Badanie pokazało, że media wywierają silny wpływ na opinię publiczną.", "The survey revealed that public opinion is strongly influenced by the media.");
insert into sentences (id, bookId, chapterId, pl, en) value(7819, 11, 15, "Najnowsze badanie na temat przyczyn tej choroby obaliło wcześniejsze teorie.", "The latest research into causes of this disease debunked previous theories.");
insert into sentences (id, bookId, chapterId, pl, en) value(7820, 11, 15, "Dopuszczalna granica błędu, zwykle stosowana przez badaczy, to 4%.", "The acceptable margin of error usually used by researchers is 4%.");
insert into sentences (id, bookId, chapterId, pl, en) value(7821, 11, 15, "Zanim produkt zostanie wypuszczony na rynek, musi on być przetestowany na kilka sposobów.", "Before a product is rolled out, it has to be tested in several ways.");
insert into sentences (id, bookId, chapterId, pl, en) value(7822, 11, 15, "Firmy farmaceutyczne przeprowadzają testy i próby kliniczne, żeby sprawdzić, czy leki są skuteczne i bezpieczne.", "Pharmaceutical companies conduct tests and clinical trials to check whether drugs are effective and safe.");
insert into sentences (id, bookId, chapterId, pl, en) value(7823, 11, 15, "Nasi badacze zebrali dużo statystyk i próbek, które teraz będą wysłane do laboratorium.", "Our researchers collected a lot of statistics and samples, which now will be sent to the laboratory.");
insert into sentences (id, bookId, chapterId, pl, en) value(7824, 11, 15, "Ich wyniki zostały zakwestionowane przez komisję.", "Their results were questioned by the committee.");
insert into sentences (id, bookId, chapterId, pl, en) value(7825, 11, 15, "Twierdzą oni, że granica błędu była za wysoka, stąd wnioski nie są wiarygodne.", "They claim that the margin of error was too high, hence the findings are not reliable.");
insert into sentences (id, bookId, chapterId, pl, en) value(7826, 11, 15, "W jego najsławniejszej pracy badawczej, próbował udowodnić swoją teorię, która okazała się być oparta na przypuszczeniach.", "In his most famous research work, he tried to prove his theory, which turned out to be based on presumptions.");
insert into sentences (id, bookId, chapterId, pl, en) value(7827, 11, 15, "Okazuje się, że mieli za mało dowodów, żeby poprzeć taką teorię.", "It turns out that they had too little evidence to support such a theory.");
insert into sentences (id, bookId, chapterId, pl, en) value(7828, 11, 15, "Przeprowadziliśmy dziesiątki studiów przypadku.", "We’ve carried out dozens of case studies.");
insert into sentences (id, bookId, chapterId, pl, en) value(7829, 11, 15, "Na podstawie naszych obserwacji, wyciągniemy wnioski.", "Based on our observations, we will draw conclusions.");
