insert into sentences (id, bookId, chapterId, pl, en) value(7835, 11, 16, "Zdecydowaliśmy się wziąć pożyczkę w dolarach, ponieważ kurs walutowy jest korzystny.", "We’ve decided to take out a loan in dollars because the exchange rate is favourable.");
insert into sentences (id, bookId, chapterId, pl, en) value(7836, 11, 16, "Stopy procentowe mogą spaść. Nie wolałbyś poczekać?", "The interest rates might fall. Wouldn’t you prefer to wait?");
insert into sentences (id, bookId, chapterId, pl, en) value(7837, 11, 16, "Pożyczyliśmy trochę pieniędzy, ale nie mogliśmy spłacić długu na czas.", "We borrowed some money, but we couldn’t pay off the debt on time.");
insert into sentences (id, bookId, chapterId, pl, en) value(7838, 11, 16, "Niestety bank odmówił przedłużenia terminu spłaty.", "Unfortunately, the bank refused to roll it over.");
insert into sentences (id, bookId, chapterId, pl, en) value(7839, 11, 16, "Jeśli nie zapłacimy długu wraz z zaległymi odsetkami, obciążą naszego poręczyciela.", "If we don’t pay off the debt together with its overdue interest, they will charge our guarantor.");
insert into sentences (id, bookId, chapterId, pl, en) value(7840, 11, 16, "Rok temu kupiliśmy samochód w systemie ratalnym. Teraz spłacamy go w ratach. ", "A year ago we bought a car on hire purchase. Now we’re paying it off in instalments.");
insert into sentences (id, bookId, chapterId, pl, en) value(7841, 11, 16, "Nie powinniśmy byli wybierać takiej sumy pieniędzy. Teraz mamy saldo ujemne.", "We shouldn’t have withdrawn so much money. Now, we are overdrawn.");
insert into sentences (id, bookId, chapterId, pl, en) value(7842, 11, 16, "W niektórych krajach, oferuje się rodzinom nieoprocentowane pożyczki.", "In some countries families are offered interest-free loans.");
