insert into sentences (id, bookId, chapterId, pl, en) value(7847, 11, 16, "Nie lubię nosić pieniędzy przy sobie. Zawsze wpłacam je w najbliższym mi oddziale.", "I don’t like carrying money on me. I always make a deposit at my nearest branch.");
insert into sentences (id, bookId, chapterId, pl, en) value(7848, 11, 16, "Niektórym dostawcom płacimy przez stałe zlecenie. Jest szybciej i łatwiej.", "We pay some providers by direct debit. It’s faster and easier.");
insert into sentences (id, bookId, chapterId, pl, en) value(7849, 11, 16, "Wypisał mi czek, ale kiedy chciałem go zrealizować, nie przyjęło go/był bez pokrycia.", "He made out a cheque for me, but when I wanted to cash it, it bounced.");
insert into sentences (id, bookId, chapterId, pl, en) value(7850, 11, 16, "Możesz wziąć pożyczkę albo o stałym, albo o zmiennym oprocentowaniu.", "You can take out either a fixed or floating rate loan.");
insert into sentences (id, bookId, chapterId, pl, en) value(7851, 11, 16, "Czy banki pobierają opłatę za otwarcie konta bankowego?", "Do banks charge a fee for opening a bank account?");
insert into sentences (id, bookId, chapterId, pl, en) value(7852, 11, 16, "Lepiej jest trzymać pieniądze na koncie oszczędnościowym, ponieważ narosną odsetki.", "It’s better to keep your money in a savings account because the interest will accrue.");
insert into sentences (id, bookId, chapterId, pl, en) value(7853, 11, 16, "Czy uregulowałeś już wszystkie rachunki?", "Have you settled all the bills yet?");
