insert into sentences (id, bookId, chapterId, pl, en) value(7856, 11, 17, "Ich oferta była nienegocjowalna. Nawet nie chcieli wchodzić w negocjacje.", "Their offer was non-negotiable. They didn’t even want to enter into negotiations.");
