insert into sentences (id, bookId, chapterId, pl, en) value(7859, 11, 17, "Rozmowy dotyczące przejęcia będą wznowione w przyszłym miesiącu.", "Takeover talks will be resumed next month.");
insert into sentences (id, bookId, chapterId, pl, en) value(7860, 11, 17, "Decyzja była jednogłośna i korzystna dla obu stron.", "The decision was unanimous and advantageous to both sides.");
insert into sentences (id, bookId, chapterId, pl, en) value(7861, 11, 17, "Zarząd i związki zawodowe negocjują płace już od 3 miesięcy.", "The management and trade unions have been negotiating wages for 3 months now.");
insert into sentences (id, bookId, chapterId, pl, en) value(7862, 11, 17, "W końcu osiągnęli porozumienie.", "Finally they reached an agreement.");
insert into sentences (id, bookId, chapterId, pl, en) value(7863, 11, 17, "Związki zawodowe działają jako rozjemca pomiędzy pracownikami i zarządem.", "Trade unions act as a mediator between workers and the management.");
