insert into sentences (id, bookId, chapterId, pl, en) value(7865, 11, 17, "W końcu dogadaliśmy się z zarządem.", "Finally we made a deal with the management.");
insert into sentences (id, bookId, chapterId, pl, en) value(7866, 11, 17, "Jeśli będziesz się z nimi za bardzo targował, istnieje ryzyko, że się wycofają z interesu.", "If you bargain too much with them, there’s a risk that they will back out of the deal.");
insert into sentences (id, bookId, chapterId, pl, en) value(7867, 11, 17, "Po zaciekłych negocjacjach, zdołaliśmy zawrzeć umowę.", "After heated negotiations, we managed to conclude a deal/an agreement.");
insert into sentences (id, bookId, chapterId, pl, en) value(7868, 11, 17, "Został ogłoszony przetarg na szkolenie kadry.", "Staff training has been put out to tender.");
insert into sentences (id, bookId, chapterId, pl, en) value(7869, 11, 17, "Jako że posiadamy odpowiednie doświadczenie zawodowe i kwalifikacje, złożyliśmy ofertę.", "As we have appropriate professional experience and qualifications, we submitted a bid.");
insert into sentences (id, bookId, chapterId, pl, en) value(7870, 11, 17, "Ostatecznie, szef zmienił zdanie i wycofaliśmy się, bo konkurencja była za mocna.", "In the end, the boss changed his mind and we backed out because the competition was too tough.");
insert into sentences (id, bookId, chapterId, pl, en) value(7871, 11, 17, "Przeczytałem warunki umowy/kontraktu.", "I’ve read the terms and conditions of the contract.");
insert into sentences (id, bookId, chapterId, pl, en) value(7872, 11, 17, "Zastanawiam się, czy są one negocjowalne.", "I’m wondering whether they are negotiable.");
insert into sentences (id, bookId, chapterId, pl, en) value(7873, 11, 17, "W zależności od warunków płatności, jakie nam oferujecie, możemy obniżyć trochę cenę.", "Depending on the payment conditions that you offer us, we can lower the price a little bit.");
insert into sentences (id, bookId, chapterId, pl, en) value(7874, 11, 17, "Czasami trudno jest osiągnąć kompromis, szczególnie gdy ktoś jest twardym negocjatorem.", "Sometimes it’s hard to reach a compromise, especially if someone is a tough negotiator.");
insert into sentences (id, bookId, chapterId, pl, en) value(7875, 11, 17, "Jak zamierzacie rozstrzygnąć ten spór?", "How are you going to settle this dispute?");
insert into sentences (id, bookId, chapterId, pl, en) value(7876, 11, 17, "Pracownicy zgłosili żądania, na które zarząd nie mógł przystać.", "Employees made demands that the management couldn’t concede to.");
insert into sentences (id, bookId, chapterId, pl, en) value(7877, 11, 17, "Zagrozili, że zastrajkują i zarząd był zmuszony ustąpić.", "They threatened to go on strike and the management was forced to back down.");
insert into sentences (id, bookId, chapterId, pl, en) value(7878, 11, 17, "Musieli pójść na ustępstwa, żeby uniknąć strajku.", "They had to make concessions to avoid a strike.");
insert into sentences (id, bookId, chapterId, pl, en) value(7879, 11, 18, "Aktywa obrotowe zawierają pieniądze, które firma ma na koncie bankowym, należności i zapasy towarów.", "Current assets include money that a company has in its bank account, receivables and stocks of goods.");
insert into sentences (id, bookId, chapterId, pl, en) value(7880, 11, 18, "Wszystkie budynki, maszyny i ziemia należące do firmy są jej środkami trwałymi.", "All the company’s buildings, machines and land are its fixed assets.");
insert into sentences (id, bookId, chapterId, pl, en) value(7881, 11, 18, "Aktywa niematerialne zawierają, między innymi, marki, licencje i patenty.", "Intangible assets include, among others, brands, licences and patents.");
insert into sentences (id, bookId, chapterId, pl, en) value(7882, 11, 18, "Są dwa rodzaje pasywów: pasywa bieżące i długoterminowe.", "There are two kinds of liabilities: current and long-term liabilities.");
insert into sentences (id, bookId, chapterId, pl, en) value(7883, 11, 18, "Pod koniec każdego roku obrotowego, nasz księgowy przygotowuje bilans, który przedstawia wszystkie aktywa i pasywa firmy.", "At the end of every financial year, our accountant prepares a balance sheet, which presents all the company’s assets and liabilities.");
