insert into sentences (id, bookId, chapterId, pl, en) value(7887, 11, 18, "Zajmują się należnościami oraz zobowiązaniami.", "They handle accounts receivable and accounts payable.");
insert into sentences (id, bookId, chapterId, pl, en) value(7888, 11, 18, "Jeśli potrzebują pomocy, zwracają się do starszego księgowego.", "If they need help, they turn to the senior accountant.");
insert into sentences (id, bookId, chapterId, pl, en) value(7889, 11, 18, "Jeśli kupujesz sprzęt warty więcej niż 1000 euro, musisz go amortyzować przez pewien okres czasu.", "When you buy equipment that is worth more than 1000 euros, you have to depreciate it over some period of time.");
insert into sentences (id, bookId, chapterId, pl, en) value(7890, 11, 18, "Jak się zużywa, część jego wartości jest dodawana do kosztów.", "As it wears out, a part of its value is added to expenses.");
insert into sentences (id, bookId, chapterId, pl, en) value(7891, 11, 18, "Pod koniec tego okresu, umarzamy pozostałą wartość księgową tego sprzętu.", "At the end of this period, we write off the remaining book value of the equipment.");
insert into sentences (id, bookId, chapterId, pl, en) value(7892, 11, 18, "Jeśli prowadzisz swoją firmę, możesz prowadzić księgi sam lub wynająć biuro rachunkowe.", "If you run your own company, you can either keep the accounts by yourself or hire an accounting office.");
insert into sentences (id, bookId, chapterId, pl, en) value(7893, 11, 18, "Dobrze jest znać prawo podatkowe i wiedzieć, jak wykorzystać wszystkie odliczenia podatkowe.", "It’s good to know the tax law and know how to take advantage of all the tax deductions.");
insert into sentences (id, bookId, chapterId, pl, en) value(7894, 11, 18, "Przygotuj wszystkie sprawozdania finansowe na audyt.", "Prepare all the financial statements for the audit.");
insert into sentences (id, bookId, chapterId, pl, en) value(7895, 11, 18, "Roczny audyt zweryfikuje, czy księgi, które prowadzimy, są poprawne i dokładne.", "The annual audit will verify if the books we keep are correct and accurate.");
insert into sentences (id, bookId, chapterId, pl, en) value(7896, 11, 18, "Już wprowadziliśmy wszystkie dane liczbowe do księgi głównej.", "We’ve already entered all the figures in the general ledger.");
insert into sentences (id, bookId, chapterId, pl, en) value(7897, 11, 18, "Wykres sprzedaży wskazuje, że powinniśmy wkrótce osiągnąć próg rentowności.", "The sales chart shows that we should reach the break-even point soon.");
insert into sentences (id, bookId, chapterId, pl, en) value(7898, 11, 18, "Jeśli odejmiemy sumę bieżących pasywów od aktywów obrotowych, otrzymamy kapitał obrotowy.", "If we subtract the sum of current liabilities from the current assets, we’ll get the working capital.");
insert into sentences (id, bookId, chapterId, pl, en) value(7899, 11, 18, "Jak długo amortyzuje się samochód?", "How long is a car depreciated for?");
insert into sentences (id, bookId, chapterId, pl, en) value(7900, 11, 18, "Powinno się amortyzować samochody służbowe przez okres 3 lat.", "You should depreciate company cars over a period of 3 years.");
