insert into sentences (id, bookId, chapterId, pl, en) value(7902, 11, 18, "No cóż, to co możemy zrobić, to umorzyć ich długi i zaksięgować je jako koszt.", "Well, what we can do is write their debts off and enter them as an expense.");
insert into sentences (id, bookId, chapterId, pl, en) value(7903, 11, 19, "Żeby zrealizować ten projekt, wymagane są następujące zasoby.", "To carry out this project, the following resources are required.");
