insert into sentences (id, bookId, chapterId, pl, en) value(7906, 11, 19, "To jest całkiem prawdopodobne, że projekt zostanie oddany w drugim kwartale przyszłego roku.", "It’s quite likely that the project will be delivered in the second quarter of next year.");
insert into sentences (id, bookId, chapterId, pl, en) value(7907, 11, 19, "Czy zarys projektu został już sporządzony?", "Has the project outline been drawn up yet?");
