insert into sentences (id, bookId, chapterId, pl, en) value(7921, 11, 19, "Na początku, ważne jest określenie finalnych wyników projektu.", "At the beginning, it’s important to define final project deliverables.");
insert into sentences (id, bookId, chapterId, pl, en) value(7922, 11, 19, "Kamienie milowe projektu używane są do monitorowania postępu na ścieżce krytycznej.", "Project milestones are used to monitor progress on the critical path.");
insert into sentences (id, bookId, chapterId, pl, en) value(7923, 11, 19, "Ścieżka krytyczna prezentuje porządek pracy, którego powinniśmy się trzymać, po to żeby skończyć pracę na czas.", "The critical path presents the order of work that we should stick to in order to finish work on time.");
insert into sentences (id, bookId, chapterId, pl, en) value(7924, 11, 19, "Problemy finansowe zakłóciły postęp projektu i dlatego nie zdołaliśmy dotrzymać terminu.", "Financial problems impeded the progress of the project and that’s why we didn’t manage to meet the deadline.");
