insert into sentences (id, bookId, chapterId, pl, en) value(7963, 11, 21, "Zawsze przyklejamy na opakowaniu etykietę, która mówi: Delikatne. Obchodzić się ostrożnie.", "We always glue a label on the packaging saying: Fragile. Handle with care.");
