insert into sentences (id, bookId, chapterId, pl, en) value(8021, 11, 23, "Dla mnie przyjazne środowisko pracy jest ważniejsze niż wynagrodzenie i dodatki socjalne.", "For me, a friendly working environment is more important than the salary and fringe benefits.");
