insert into sentences (id, bookId, chapterId, pl, en) value(8664, 12, 15, "Podzieliliśmy projekt na siedem etapów. Niektóre kroki pokrywają się, żeby go przyspieszyć.", "We have divided the project into seven stages. Some of the steps overlap to speed it up.");
insert into sentences (id, bookId, chapterId, pl, en) value(8665, 12, 15, "Rozpoczniemy to przedsięwzięcie od projektu pilotażowego, żeby zobaczyć, do jakiego stopnia jest wykonalny.", "We will start this undertaking with a pilot project to see to what extent it’s viable.");
insert into sentences (id, bookId, chapterId, pl, en) value(8666, 12, 15, "Szacowanie kosztów jest tak samo ważne, jak planowanie zakresu. Są one częściami parametrów projektu.", "Cost estimating is as important as scope planning. They are parts of the Project Management Triangle.");
insert into sentences (id, bookId, chapterId, pl, en) value(8667, 12, 15, "Fundusze na ten projekt pochodzą z 3 źródeł: emisja nowych akcji, pożyczka bankowa i sponsoring.", "The funds for the project come from 3 sources: a new share issue, bank loan and sponsorship.");
insert into sentences (id, bookId, chapterId, pl, en) value(8668, 12, 15, "Energooszczędny model przemawia do mnie bardziej niż każdy inny.", "The energy-saving design appeals to me more than any other.");
insert into sentences (id, bookId, chapterId, pl, en) value(8669, 12, 15, "Struktura podziału pracy jest narzędziem, które pomaga menedżerowi projektu kontrolować na bieżąco wszystkie jego etapy.", "Work breakdown structure is a tool that helps the project manager keep track of all the stages.");
insert into sentences (id, bookId, chapterId, pl, en) value(8670, 12, 15, "Gdy pracujesz nad projektem, musisz sporządzić plan pracy i podzielić nakład pracy na zadania.", "When you work on a project, you have to draw up a work schedule and divide the workload into tasks.");
insert into sentences (id, bookId, chapterId, pl, en) value(8671, 12, 15, "Jeśli okaże się, że jest bardzo mało zapasu w budżecie, nie będziemy w stanie przyjąć nowych projektów.", "If it turns out that there’s very little slack in the budget, we won’t be able to take on any more projects.");
insert into sentences (id, bookId, chapterId, pl, en) value(8672, 12, 15, "Plan nie został uznany za wykonalny, dlatego został odrzucony.", "The plan was not considered feasible, hence it was rejected.");
insert into sentences (id, bookId, chapterId, pl, en) value(8673, 12, 15, "Gdybyśmy skupili się na naszych głównych zadaniach, zamiast zajmować się wszystkim na raz, osiągnęlibyśmy wcześniej ustalone cele.", "If we had focused on our core tasks instead of dealing with everything at a time, we would have met our pre-defined objectives.");
insert into sentences (id, bookId, chapterId, pl, en) value(8674, 12, 15, "Oszacowanie zasobów jest jednym z kluczowych zadań w zarządzaniu projektami.", "Estimation of resources is one of the key tasks of project management.");
insert into sentences (id, bookId, chapterId, pl, en) value(8675, 12, 16, "Zaoferowano nam wieloletnią umowę na dostawę produktów i usług świadczonych przez naszą firmę.", "We were offered a long-standing agreement to supply products and services rendered by our company.");
insert into sentences (id, bookId, chapterId, pl, en) value(8676, 12, 16, "Umowa wciąż obowiązuje i przewiduje 3-miesięczny okres wypowiedzenia.", "That agreement still stands and stipulates a 3-month notice period.");
insert into sentences (id, bookId, chapterId, pl, en) value(8677, 12, 16, "To oznacza, że powinni państwo dać nam 3-miesięczne wypowiedzenie w przypadku zerwania umowy.", "That means that you’re supposed to give us 3-months’ notice in case of breaking the agreement.");
insert into sentences (id, bookId, chapterId, pl, en) value(8678, 12, 16, "Jeśli podpiszemy umowę wyłączności i będziemy waszymi jedynymi dostawcami, jesteśmy chętni zaoferować 10% rabatu na wszystkie zamówienia.", "If we sign the exclusivity agreement and we’re your sole suppliers, we’re willing to offer you a 10% discount on all orders.");
