insert into sentences (id, bookId, chapterId, pl, en) value(8680, 12, 16, "Poprosiliśmy trzech potencjalnych dostawców o szczegółową wycenę, zawierającą warunki dostawy, ceny oraz rabaty.", "We’ve asked three potential suppliers for a detailed quotation including delivery terms, prices and discounts.");
insert into sentences (id, bookId, chapterId, pl, en) value(8681, 12, 16, "Jeśli twoja firma zostanie przejęta, może to doprowadzić do zwolnień w fabryce.", "If our company is taken over, this could result in redundancies at the factory.");
insert into sentences (id, bookId, chapterId, pl, en) value(8682, 12, 16, "Warunki kontraktu przewidują sposób płatności i dystrybucji.", "The contract terms stipulate payment and distribution methods.");
insert into sentences (id, bookId, chapterId, pl, en) value(8683, 12, 16, "Jak już uzgodnimy warunki, umowa zostanie zawarta błyskawicznie.", "Once we agree terms and conditions, the deal will be concluded in no time.");
insert into sentences (id, bookId, chapterId, pl, en) value(8684, 12, 16, "Jeśli chodzi o cenę exwork, to kupujący ponosi koszt i ryzyko związane z transportem towarów.", "When it comes to ex-works price, it’s the buyer who bears the cost and risk of transporting the goods.");
