insert into sentences (id, bookId, chapterId, pl, en) value(8688, 12, 16, "Te dwie firmy połączyły siły, żeby zapewnić najwyższej klasy usługi w dwóch tysiącach sklepów na całym świecie.", "These two companies have teamed up to provide top quality services in 2k stores around the world.");
insert into sentences (id, bookId, chapterId, pl, en) value(8689, 12, 16, "Pomimo tego, że zaoferowali bardzo dobrą cenę, negocjacje stanęły w martwym punkcie.", "Despite the fact they offered a very good price, negotiations reached a deadlock.");
insert into sentences (id, bookId, chapterId, pl, en) value(8690, 12, 16, "Lepiej dla nas, gdybyśmy zatrudnili podwykonawcę.", "We’d be better off hiring a subcontractor. ");
insert into sentences (id, bookId, chapterId, pl, en) value(8691, 12, 16, "Jeśli uznamy ich żądania, powstanie efekt domina i pogorszy to sytuację finansową.", "If we concede to their demands, it’ll have a knock-on effect and worsen the financial situation.");
insert into sentences (id, bookId, chapterId, pl, en) value(8692, 12, 16, "Z powodu nieuczciwej konkurencji, negocjacje zostały przerwane.", "Due to unfair competition, the negotiations were broken off.");
insert into sentences (id, bookId, chapterId, pl, en) value(8693, 12, 16, "Światowa Organizacja Handlu pracuje nad zniesieniem barier handlowych takich jak taryfy celne i kwoty importowe.", "The World Trade Organization is working on dismantling trade barriers such as tariffs and import quotas.");
insert into sentences (id, bookId, chapterId, pl, en) value(8694, 12, 16, "W negocjacjach dotyczących fuzji, nasza firma wydawała się mieć większą siłę przetargową.", "In the merger negotiations, our company seemed to have more bargaining power.");
insert into sentences (id, bookId, chapterId, pl, en) value(8695, 12, 16, "Jeśli wszystko pójdzie zgodnie z planem, za kilka dni podpiszemy nową umowę.", "If everything goes according to plan, we’ll sign the new contract in a couple of days.");
insert into sentences (id, bookId, chapterId, pl, en) value(8696, 12, 16, "Zdołaliśmy wygrać ten przetarg pomimo silnej konkurencji.", "We managed to win the contract/tender despite fierce competition.");
insert into sentences (id, bookId, chapterId, pl, en) value(8697, 12, 16, "Nasze ceny są o 30% niższe od innych konkurencyjnych ofert, wahających się od 40 do 60 euro.", "Our prices are 30% lower than other competitive offers, ranging from 40 to 60 euros.");
insert into sentences (id, bookId, chapterId, pl, en) value(8698, 12, 16, "Ten produkt zaczął zyskiwać na popularności w połowie lat 90-tych i ostatecznie wyparł wszystkie inne.", "This product started to gain ground in the mid-nineties and eventually drove out all the others.");
insert into sentences (id, bookId, chapterId, pl, en) value(8699, 12, 17, "Naszym obowiązkiem jest prowadzenie rejestru płatności, zarówno wykonanych, jak i odebranych w każdym okresie sprawozdawczym.", "It’s our responsibility to keep a record of payments, both made and received in each accounting period.");
