insert into sentences (id, bookId, chapterId, pl, en) value(8701, 12, 17, "Tylko wtedy będziemy mogli wykorzystać nasze aktywa obrotowe do dalszego inwestowania.", "Only then will we be able to use our current assets for further investing.");
insert into sentences (id, bookId, chapterId, pl, en) value(8702, 12, 17, "Musimy prowadzić rejestr wszystkich aktywów materialnych i niematerialnych oraz pasywów.", "We have to keep a record of all the tangible and intangible assets as well as liabilities.");
