insert into sentences (id, bookId, chapterId, pl, en) value(8705, 12, 17, "Twój asystent pomoże ci w prowadzeniu ksiąg, ewidencji sprzedaży i zakupów oraz sporządzaniu sprawozdań finansowych.", "Your assistant will help you with bookkeeping, sales and expenses records as well as preparing financial statements.");
insert into sentences (id, bookId, chapterId, pl, en) value(8706, 12, 17, "Czy zyski zatrzymane wypracowane przez firmę w tym kwartale powinny być ponownie zainwestowane czy trzymane w rezerwie?", "Should retained earnings generated by the company this quarter be reinvested or kept as a reserve?");
insert into sentences (id, bookId, chapterId, pl, en) value(8707, 12, 17, "Aby obliczyć zysk operacyjny, bierzemy pod uwagę koszt własny sprzedaży oraz koszty ogólne.", "In order to calculate the operating profit, we take into account the cost of goods sold and general expenses.");
insert into sentences (id, bookId, chapterId, pl, en) value(8708, 12, 17, "Czy księga główna jest aktualizowana automatycznie po zaksięgowaniu listy płac?", "Is the general ledger automatically updated after recording payroll transactions?");
insert into sentences (id, bookId, chapterId, pl, en) value(8709, 12, 17, "Ta faktura została wystawiona tydzień temu i termin jej zapłaty przypada pod koniec miesiąca.", "This invoice was issued a week ago and is due at the end of the month.");
insert into sentences (id, bookId, chapterId, pl, en) value(8710, 12, 17, "Z upływem czasu maszyny się zużywają i tracą na wartości.", "With time, machinery wears out and declines in value.");
insert into sentences (id, bookId, chapterId, pl, en) value(8711, 12, 17, "Koszt ich amortyzacji jest wliczany do rachunku zysków i strat, do czasu aż będzie umorzony.", "Its depreciation cost is included in the P&L account until it’s written off.");
insert into sentences (id, bookId, chapterId, pl, en) value(8712, 12, 17, "Jeśli weźmiemy pożyczkę lub kupimy akcje, musimy to ująć w sprawozdaniu.", "If we take out a loan or buy shares, we need to report it in the statement.");
insert into sentences (id, bookId, chapterId, pl, en) value(8713, 12, 17, "Nie zwracaj uwagi na wartość rynkową. Interesuje nas wartość księgowa.", "Don’t pay attention to the market value. It’s the book value that we’re interested in.");
insert into sentences (id, bookId, chapterId, pl, en) value(8714, 12, 17, "Jaka jest łączna wartość bieżących i długoterminowych pasywów twojej firmy?", "What’s the total value of your company’s current and long-term liabilities?");
insert into sentences (id, bookId, chapterId, pl, en) value(8715, 12, 17, "Jaka jest różnica między podatkiem naliczonym i należnym w tym miesiącu?", "What’s the difference between input tax and output tax this month?");
insert into sentences (id, bookId, chapterId, pl, en) value(8716, 12, 17, "W deklaracjach dane liczbowe powinny być zaokrąglone do dwóch miejsc po przecinku.", "In declarations, the figures should be rounded off to two decimal places.");
insert into sentences (id, bookId, chapterId, pl, en) value(8717, 12, 17, "Czy konieczne jest, aby prowadzić księgowość według reguły podwójnego zapisu?", "Is it necessary to do double entry bookkeeping?");
insert into sentences (id, bookId, chapterId, pl, en) value(8718, 12, 17, "Tak. Każda transakcja musi być księgowana dwa razy, jako Winien (debet) i Ma.", "Yes, it is. Every transaction has to be recorded twice as a debit and as a credit.");
insert into sentences (id, bookId, chapterId, pl, en) value(8719, 12, 17, "Tutaj możemy znaleźć kwotę należności nieściągalnych, podatku odroczonego oraz niespłaconych długów.", "Here you can find the amount of bad debts, deferred tax and outstanding debts.");
insert into sentences (id, bookId, chapterId, pl, en) value(8720, 12, 17, "Zaliczka, to zwykle jedna trzecia pełnej kwoty. ", "A down payment is usually a third of the total amount.");
insert into sentences (id, bookId, chapterId, pl, en) value(8721, 12, 17, "Przepływy pieniężne netto z działalności operacyjnej, w tym przypadku są wyższe niż przepływy pieniężne netto z inwestycji.", "Net cash flow from operations in this case is higher than net cash flow from investment.");
insert into sentences (id, bookId, chapterId, pl, en) value(8722, 12, 17, "Zgodnie z zasadą memoriałową, musimy zaksięgować wszystkie transakcje do końca okresu sprawozdawczego.", "In line with the accrual basis, we must record all transactions by the end of the accounting period.");
