insert into sentences (id, bookId, chapterId, pl, en) value(8763, 12, 19, "Ostatnio zostały podjęte środki mające na celu promowanie przedsiębiorczości.", "Recently several measures aimed at promoting entrepreneurship have been taken.");
insert into sentences (id, bookId, chapterId, pl, en) value(8764, 12, 19, "W ubiegłym roku, wiele funduszy zostało przekierowanych z publicznego do prywatnego sektora przemysłu.", "Last year a lot of funds were diverted from the public to the private sector of industry.");
insert into sentences (id, bookId, chapterId, pl, en) value(8765, 12, 19, "Czy jest jeszcze coś, co może pobudzić gospodarkę i wesprzeć zrównoważony wzrost gospodarczy?", "Is there anything else that can boost the economy and promote sustainable economic growth?");
insert into sentences (id, bookId, chapterId, pl, en) value(8766, 12, 19, "W celu wypełnienia dziur w budżecie, rząd zdecydował nałożyć podatek na rolnictwo.", "In order to fill budget gaps, the government has decided to impose a tax on agriculture.");
insert into sentences (id, bookId, chapterId, pl, en) value(8767, 12, 19, "Ten projekt powinien był być przekazany do organizacji pozarządowej.", "That project should have been handed over to an NGO.");
insert into sentences (id, bookId, chapterId, pl, en) value(8768, 12, 19, "Dzięki dotacjom rządowym, nastąpiła niewielka poprawa.", "Thanks to the government grants, there’s been a slight improvement.");
insert into sentences (id, bookId, chapterId, pl, en) value(8769, 12, 19, "Te nowe przepisy powinny stymulować gospodarkę, a nie odwrotnie.", "These new regulations should stimulate the economy, not the other way round.");
insert into sentences (id, bookId, chapterId, pl, en) value(8770, 12, 19, "Naszym głównym celem w tym momencie jest przyhamowanie galopującej inflacji.", "Our main goal at the moment is to curb galloping inflation.");
insert into sentences (id, bookId, chapterId, pl, en) value(8771, 12, 20, "Wadliwe elementy musiały wyjść z tej samej partii produkcyjnej.", "The faulty pieces must have come from the same production batch.");
