insert into sentences (id, bookId, chapterId, pl, en) value(8775, 12, 20, "To jest punkt odniesienia, według którego będziemy oceniać jakość produktów.", "This is the benchmark by which we will judge the quality of the products.");
insert into sentences (id, bookId, chapterId, pl, en) value(8776, 12, 20, "Czy wolałbyś używać analizy porównawczej, czy sprawdzonych rozwiązań, żeby poprawić jakość produkcji?", "Would you prefer to use benchmarking or best practice to enhance production quality?");
insert into sentences (id, bookId, chapterId, pl, en) value(8777, 12, 20, "Łatwiej jest korzystać z metody analizy konstrukcji, niż zaczynać od zera.", "It’s easier to use reverse engineering than start from scratch.");
insert into sentences (id, bookId, chapterId, pl, en) value(8778, 12, 20, "Ten rodzaj przemysłu wymaga ogromnych inwestycji w maszyny i know–how. Co więcej, koszty eksploatacyjne są bardzo wysokie.", "This kind of industry requires massive investment in machinery and know-how. What’s more, operating costs are very high.");
insert into sentences (id, bookId, chapterId, pl, en) value(8779, 12, 20, "Jakie informacje są wymagane na wczesnych etapach w procesie produkcyjnym?", "What kind of information is required at the early stages in the production process?");
insert into sentences (id, bookId, chapterId, pl, en) value(8780, 12, 20, "Jeśli ktoś wykonywałby za nas naszą działalność dodatkową, moglibyśmy skupić się na naszej podstawowej działalności.", "If we had our non-core activities done by someone else, we would be able to focus on our core business.");
insert into sentences (id, bookId, chapterId, pl, en) value(8781, 12, 20, "Jakie trudności napotykacie przy pozbywaniu się odpadów przemysłowych?", "What difficulties do you encounter while disposing of industrial waste?");
insert into sentences (id, bookId, chapterId, pl, en) value(8782, 12, 20, "Używamy technik zapoczątkowanych przez francuskich inżynierów, które określają wszystkie etapy w cyklu produkcyjnym.", "We’re using techniques pioneered by French engineers that define all the stages in a production cycle.");
insert into sentences (id, bookId, chapterId, pl, en) value(8783, 12, 20, "Czas przygotowania samej produkcji to około 6 miesięcy, nie mówiąc już o reklamie i dystrybucji.", "The lead time for production alone is around 6 months, not to mention advertising and distribution.");
insert into sentences (id, bookId, chapterId, pl, en) value(8784, 12, 20, "Ten gotowy produkt to wynik bardzo prostego pomysłu.", "The finished product is the result of a very straightforward idea.");
insert into sentences (id, bookId, chapterId, pl, en) value(8785, 12, 20, "Jak mierzy się zdolność produkcyjną?", "How is the production capacity measured?");
insert into sentences (id, bookId, chapterId, pl, en) value(8786, 12, 20, "Robimy wyrywkowe kontrole, żeby upewnić się, że wszystko jest robione zgodnie ze specyfikacją oraz w celu uniknięcia procesu przeróbki.", "We do spot checks in order to make sure everything is done to spec and also to avoid the process of reworking.");
insert into sentences (id, bookId, chapterId, pl, en) value(8787, 12, 20, "Chcemy, by wszystko było zrobione dobrze za pierwszym razem.", "We want to have everything done well first time.");
insert into sentences (id, bookId, chapterId, pl, en) value(8788, 12, 20, "Mamy umowę licencyjną z francuską firmą na produkcję i sprzedaż ich produktów tutaj.", "We have a licensing agreement with a French company to produce and market their products here.");
insert into sentences (id, bookId, chapterId, pl, en) value(8789, 12, 20, "Z powodu nadprodukcji, rynek jest przesycony/zalany tym towarem.", "Due to overproduction, there is a glut in the market of this commodity.");
insert into sentences (id, bookId, chapterId, pl, en) value(8790, 12, 20, "Trudno powiedzieć, dlaczego model ten został przyjęty jako sprawdzone/najlepsze rozwiązanie.", "It’s hard to say why this model was adopted as best practice.");
insert into sentences (id, bookId, chapterId, pl, en) value(8791, 12, 20, "Firma podniosła swoje standardy efektywności i w ten sposób udało im się wypracować/osiągnąć najlepsze rozwiązanie.", "The company raised its standards of efficiency and this way they managed to reach best practice.");
insert into sentences (id, bookId, chapterId, pl, en) value(8792, 12, 20, "Fabryka pracuje na pełnych obrotach. Nasza całkowita produkcja wzrosła o 10%.", "The factory is working at full capacity. Our overall production has risen by 10%.");
insert into sentences (id, bookId, chapterId, pl, en) value(8793, 12, 20, "Aby odnieść sukces, potrzebujemy odpowiedniego produktu w odpowiednim miejscu i w odpowiednim czasie.", "In order to succeed, we need the right product in the right place at the right time.");
insert into sentences (id, bookId, chapterId, pl, en) value(8794, 12, 20, "W jakim przemyśle korzyści skali są szczególnie ważne?", "In what kind of industry are economies of scale particularly important?");
insert into sentences (id, bookId, chapterId, pl, en) value(8795, 12, 21, "Płatność za fakturę przypada w przyszłym tygodniu. Powinniśmy ją zapłacić na czas, jeśli nie chcemy, żeby zostały naliczone odsetki.", "The invoice is due next week. We should pay it on time if we don’t want any interest to be applied.");
insert into sentences (id, bookId, chapterId, pl, en) value(8796, 12, 21, "Nasi klienci mogą wybrać metodę płatności, którą jest albo pobranie albo płatność przy zamówieniu.", "Our clients can choose their payment method, which is either COD or CWO.");
insert into sentences (id, bookId, chapterId, pl, en) value(8797, 12, 21, "Sprzedaż krzyżowa działa bardzo dobrze na tej stronie, gdyż umożliwia zakup dodatkowych towarów i zapłatę za dostawę tylko raz.", "Cross-selling works very well on this website since it enables you to buy extra products and pay for delivery only once.");
insert into sentences (id, bookId, chapterId, pl, en) value(8798, 12, 21, "Spójrz na ten tablet! Jest najwyższej jakości i kupiłem go po obniżonej cenie. Jest warty swojej ceny.", "Look at this tablet. It’s top quality and I bought it at a reduced price. It’s great value for money.");
insert into sentences (id, bookId, chapterId, pl, en) value(8799, 12, 21, "Jeśli produkt ma wadę, opuścimy 30% z ceny brutto.", "If the product has a fault, we’ll knock 30% off the gross price.");
insert into sentences (id, bookId, chapterId, pl, en) value(8800, 12, 21, "Powinieneś dokonać jednorazowej wpłaty i wtedy będziesz miał prawo do korzystania z usługi przez 2 lata.", "You should make a one-off payment and then you’ll be entitled to use the service for 2 years.");
insert into sentences (id, bookId, chapterId, pl, en) value(8801, 12, 21, "Prosiłeś o ostatnie dane sprzedażowe. Oto arkusz kalkulacyjny, który je podsumowuje.", "You’ve asked for the latest sales figures. Here’s the spreadsheet that sums them up.");
insert into sentences (id, bookId, chapterId, pl, en) value(8802, 12, 21, "Wprowadzając jednolitą stawkę, będziemy mogli liczyć na lojalność klientów.", "By introducing a flat rate, we’ll be able to count on customer loyalty.");
insert into sentences (id, bookId, chapterId, pl, en) value(8803, 12, 21, "Klienci zbierają punkty na kartach lojalnościowych. Punkty te mogą być wykorzystane na poczet przyszłych zakupów.", "Customers collect points on their loyalty cards. These points can be redeemed against future purchases.");
