insert into sentences (id, bookId, chapterId, pl, en) value(8808, 12, 21, "Upewnij się, że dostawca jest godny zaufania, jeśli kupujesz płacąc z góry, albo wybierz płatność za pobraniem.", "Make sure that the supplier is reliable when buying on a CWO basis, or choose COD.");
