insert into sentences (id, bookId, chapterId, pl, en) value(8821, 12, 22, "Musiała nastąpić pomyłka w zamówieniu, stąd to opóźnienie w wysyłce.", "There must have been a mix-up in the order, hence the delay in dispatch.");
insert into sentences (id, bookId, chapterId, pl, en) value(8822, 12, 22, "Zgodnie z naszą polityką zwrotów, pełny koszt będzie refundowany, pod warunkiem że towary zostaną zwrócone w idealnym stanie.", "In line with our returns policy, the full cost will be refunded provided that the goods are returned in perfect condition.");
insert into sentences (id, bookId, chapterId, pl, en) value(8823, 12, 22, "Pracujemy nad wdrożeniem bardziej skutecznego systemu zaopatrzenia.", "We’re working on implementing a more efficient procurement system.");
