insert into sentences (id, bookId, chapterId, pl, en) value(8825, 12, 22, "Początkowe nakłady na reklamę były dwa razy wyższe od kosztów produkcji.", "The initial outlay on advertising was twice as high as the production costs.");
insert into sentences (id, bookId, chapterId, pl, en) value(8826, 12, 22, "Koszty ogólne związane z prowadzeniem małej lub średniej firmy są relatywnie wyższe.", "Overheads involved in running a small or medium company are relatively higher.");
insert into sentences (id, bookId, chapterId, pl, en) value(8827, 12, 22, "Firma, która wygrała przetarg, zbyt nisko oszacowała koszty projektu i wycofała się.", "The company that won the tender underestimated the cost of the project and withdrew.");
insert into sentences (id, bookId, chapterId, pl, en) value(8828, 12, 22, "Niektóre koszty bieżące mogą być zrekompensowane poprzez cięcia w innych obszarach.", "Some of the running costs could be offset by cutbacks in other areas.");
insert into sentences (id, bookId, chapterId, pl, en) value(8829, 12, 22, "Wyłożyliśmy 300 000 euro na tę kampanię reklamową. Ciekawe, czy to się opłaci.", "We shelled out 300 000 euros for that advertising campaign. I wonder whether it will pay off.");
insert into sentences (id, bookId, chapterId, pl, en) value(8830, 12, 22, "Policzyli sobie dwa razy więcej niż wynosi aktualna stawka za tego rodzaju usługę, ale było warto.", "They charged us twice the going rate for this kind of service but it was worth it.");
insert into sentences (id, bookId, chapterId, pl, en) value(8831, 12, 22, "Dostawa jest niekompletna. Co więcej, dane techniczne nie są zgodne z zamówieniem.", "The delivery is incomplete. What’s more, the specifications are not in accordance with the order.");
