insert into sentences (id, bookId, chapterId, pl, en) value(8833, 12, 22, "Niestety, musimy ograniczyć wydatki nie tylko na badania i rozwój, ale także wydatki inwestycyjne.", "Unfortunately, we have to cut back not only on R&D spending but also capital expenditure.");
insert into sentences (id, bookId, chapterId, pl, en) value(8834, 12, 22, "Wszystkie produkty zostały wyszczególnione na fakturze, wraz z ich kodem kreskowym i ceną zakupu netto.", "All the products have been itemized on the invoice, together with their bar code and net purchase price.");
insert into sentences (id, bookId, chapterId, pl, en) value(8835, 12, 22, "Obserwowanie nawyków zakupowych pozwala nam zaoferować klientom usługi i produkty dopasowane do ich potrzeb.", "Observing buying habits lets us offer services and products tailored to our customers’ needs.");
insert into sentences (id, bookId, chapterId, pl, en) value(8836, 12, 22, "Koszty bezpośrednie są bezpośrednio związane z produktem, np. materiały, praca i sprzęt.", "Direct costs are directly attributable to the product, e.g. materials, labour and equipment.");
insert into sentences (id, bookId, chapterId, pl, en) value(8837, 12, 22, "Trudno obliczyć średni koszt z tak wielu zmiennych.", "It’s hard to calculate the average cost with so many variables.");
insert into sentences (id, bookId, chapterId, pl, en) value(8838, 12, 22, "Jako że koszty stałe zostały już ograniczone, możemy spróbować obniżyć koszty zmienne.", "As overheads have already been cut back, we can try to cut down on variable costs.");
