insert into sentences (id, bookId, chapterId, pl, en) value(8840, 12, 22, "Zamówienie musi zawierać numer i datę zamówienia, nazwę sprzedającego, szczegóły wysyłki, warunki płatności i opis produktu.", "A purchase order has to include the PO number and date, vendor’s name, shipment details, payment terms and item description.");
insert into sentences (id, bookId, chapterId, pl, en) value(8841, 12, 22, "Zawiera też cenę jednostkową, ilość, wartość, opłaty za przewóz, podatek od sprzedaży i łączną kwotę.", "It also includes unit price, quantity, amount, freight charges, sales tax and total amount.");
insert into sentences (id, bookId, chapterId, pl, en) value(8842, 12, 22, "Jeśli chcielibyście wypróbować ten program, możemy go wysłać do państwa na próbę (z prawem zwrotu).", "If you’d like to try out the programme, we can send it to you on approval.");
insert into sentences (id, bookId, chapterId, pl, en) value(8843, 12, 23, "Jesteśmy zmuszeni zamknąć fabrykę ze względu na ciągłe straty.", "We are obliged to close down the factory due to persistent losses.");
insert into sentences (id, bookId, chapterId, pl, en) value(8844, 12, 23, "Z jednej strony jestem za rozwojem technologicznym, ale z drugiej strony, może on zwiększać bezrobocie.", "On the one hand I’m in favour of technological development, but on the other, it might increase unemployment.");
insert into sentences (id, bookId, chapterId, pl, en) value(8845, 12, 23, "Firma posiada dwa miliony funtów zamrożonego kapitału i ma problemy z przepływem gotówki.", "The company has 2 million pounds of frozen capital and is experiencing cash-flow problems.");
insert into sentences (id, bookId, chapterId, pl, en) value(8846, 12, 23, "Z powodu wady konstrukcyjnej, wszystkie produkty z tej gamy musiały zostać wycofane.", "Due to a design fault, all the goods in this range had to be recalled.");
insert into sentences (id, bookId, chapterId, pl, en) value(8847, 12, 23, "Pana reklamacja zostało wyjaśniona i pełna kwota zwrócona na pański rachunek.", "Your complaint has been cleared up and the full amount refunded to your account.");
