insert into sentences (id, bookId, chapterId, pl, en) value(8849, 12, 23, "Z powodu problemów finansowych, menedżer HR poprosił niektórych pracowników, aby poszli na bezpłatny urlop.", "Due to financial problems, the HR manager asked some of the staff to go on unpaid leave.");
insert into sentences (id, bookId, chapterId, pl, en) value(8850, 12, 23, "Postanowiliśmy okroić mniej rentowne usługi, jak również zwiększyć eksport.", "We’ve decided to axe less profitable services as well as boost export.");
insert into sentences (id, bookId, chapterId, pl, en) value(8851, 12, 23, "Naszym celem jest, aby postawić firmę na nogi poprzez układ z wierzycielami.", "Our objective is to get the company back on its feet through an arrangement with creditors.");
insert into sentences (id, bookId, chapterId, pl, en) value(8852, 12, 23, "Zrobili wszystko, aby ograniczyć wydatki, ale w końcu i tak skończyło się to dla nich bankructwem.", "They did their best to retrench, but in the end they wound up bankrupt.");
insert into sentences (id, bookId, chapterId, pl, en) value(8853, 12, 23, "Przeformułowaliśmy plan oszczędnościowy i zdecydowaliśmy się przenieść produkcję do krajów rozwijających się.", "We’ve reformulated the savings plan and we’ve decided to shift production to developing countries.");
