insert into sentences (id, bookId, chapterId, pl, en) value(9081, 12, 32, "Najwyższy czas, żebyśmy się już zdecydowali. W przeciwnym razie będziemy tu siedzieć aż do północy.", "It’s high time we made up our mind. Otherwise, we’ll be here till midnight.");
insert into sentences (id, bookId, chapterId, pl, en) value(9082, 12, 32, "Podsumujmy. Pomimo tego że problem nie wydaje się być istotny, powinniśmy rozwiązać go, zanim będzie za późno.", "Let’s recap. Despite the fact that the problem does not seem significant, we should sort it out before it’s too late.");
insert into sentences (id, bookId, chapterId, pl, en) value(9083, 12, 33, "Jako wprowadzenie, chciałbym pokazać wyniki badań przeprowadzonych w zeszłym miesiącu.", "As a lead-in, I would like to show you the results of research carried out last month.");
insert into sentences (id, bookId, chapterId, pl, en) value(9084, 12, 33, "Kto chciałby zacząć?", "Who’d like to kick off?");
