insert into sentences (id, bookId, chapterId, pl, en) value(9098, 12, 33, "Jeżeli o mnie chodzi, zanim będziemy mogli podjąć jakąkolwiek decyzję, lepiej poznać wszystkie opcje, jakie mamy.", "As far as I’m concerned, before we can make any decision, we have to get a better idea of all the options we have.");
insert into sentences (id, bookId, chapterId, pl, en) value(9099, 12, 33, "Ujmijmy to w ten sposób. Możemy dopasować cenę i dać wam 10% rabatu.", "Let’s put it this way. We can match the price and give you a 10% discount.");
